      if (document.getElementById(field)) document.getElementById(field).className = "form-control is-invalid";
      if (document.getElementById(errorField)) document.getElementById(errorField).innerHTML = '<p>' + message + ' </p>';
    } else {
      if (document.getElementById(field)) document.getElementById(field).className = "form-control";
      if (document.getElementById(errorField)) document.getElementById(errorField).innerHTML = null;
    }
  }

