  getUseOfVessel(type) {
    return new Promise((resolve, reject) => {
      setTimeout(function () {
        var SelectedId = type;
        var data = {
          stringmaptype: 'USEOFVESSEL',
          orderby: 'StringMapName'
        };

        if (SelectedId === "Commercial Vessels") {
          data = {
            stringmaptype: 'COMMERCIALUSEOFVESSEL',
            orderby: 'StringMapName'
          };
        }

        api.post(APIConstant.path.dropdownbycode, data).then(res => {
          if (res.success) {
            resolve(res.data);
          } else {
            reject(0);
          }
        }).catch(err => {
          reject(0);
        });
      }, 100);
    });
  }

